sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue ) {
      return;
	}
	var check = false;
    var ga = new GlideAjax('GetEmailAddress');
	ga.addParam('sysparm_name','getEmailID');
	ga.addParam('sysparm_userId',newValue);


	ga.getXML(populateEmailField);
	function populateEmailField(response){
		var eMailFromScriptInclude = response.responseXML.documentElement.getAttribute('answer');
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email', eMailFromScriptInclude);
		g_form.addInfoMessage('Requested for email has been auto populated based on Request for field value to:' + eMailFromScriptInclude);

	}
   //Type appropriate comment here, and begin script below

}
